INSERT INTO `items` VALUES (503, 20, '1.3.6.1.2.1.1.2.0', 10, 'SNMP: System object ID', 'system.objectid[sysObjectID.0]', '15m', '2w', '0', 0, 1, '', '', '', '', NULL, NULL, '', '', 0, '', '', '', '', 0, NULL, 'MIB: SNMPv2-MIB\r\nThe vendor\'s authoritative identification of the network management subsystem contained in the entity.  This value is allocated within the SMI enterprises subtree (1.3.6.1.4.1) and provides an easy and unambiguous means for determining`what kind of box\' is being managed.  For example, if vendor`Flintstones, Inc.\' was assigned the subtree1.3.6.1.4.1.4242, it could assign the identifier 1.3.6.1.4.1.4242.1.1 to its `Fred Router\'.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, 'e03176a55c2b43e99520fca17e88e344', 'SNMP: SYSTEM OBJECT ID');
