INSERT INTO `items` VALUES (914, 11, '', 20, 'Local database discovery', 'db.odbc.discovery[local_db,\"{$MSSQL.DSN}\"]', '1h', '90d', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT ag.name AS group_name, \r\n  arcs.replica_server_name AS replica_name,\r\n  db_name(drs.database_id) AS dbname, \r\n  drs.is_local\r\nFROM sys.dm_hadr_database_replica_states drs \r\nJOIN sys.dm_hadr_availability_replica_cluster_states arcs\r\n  ON arcs.replica_id = drs.replica_id\r\nJOIN sys.availability_groups ag \r\n  ON ag.group_id = arcs.group_id\r\nJOIN sys.dm_hadr_availability_replica_states ars \r\n  ON ars.replica_id = arcs.replica_id\r\nWHERE drs.is_local = 1', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 1, NULL, 'Discovery of the local availability databases.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, 'f7395d344b67423dadcefe3f94ad159d', 'LOCAL DATABASE DISCOVERY');
INSERT INTO `items` VALUES (915, 11, '', 20, 'Mirroring discovery', 'db.odbc.discovery[mirrors,\"{$MSSQL.DSN}\"]', '1h', '90d', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT db_name(database_id) as dbname\r\nFROM sys.database_mirroring\r\nWHERE mirroring_state_desc IS NOT NULL', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 1, NULL, 'To see the row for a database other than master or tempdb, you must\r\neither be the database owner or have at least ALTER ANY DATABASE or VIEW ANY\r\nDATABASE server-level permission or CREATE DATABASE permission in the master\r\ndatabase. To see non-NULL values on a mirror database, you must be a member\r\nof the sysadmin fixed server role.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '8354506d23b241c689079eb8ff1acb72', 'MIRRORING DISCOVERY');
INSERT INTO `items` VALUES (916, 11, '', 20, 'Non-local database discovery', 'db.odbc.discovery[non-local_db,\"{$MSSQL.DSN}\"]', '1h', '90d', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT ag.name AS group_name, \r\n  arcs.replica_server_name AS replica_name,\r\n  db_name(drs.database_id) AS dbname, \r\n  drs.is_local\r\nFROM sys.dm_hadr_database_replica_states drs \r\nJOIN sys.dm_hadr_availability_replica_cluster_states arcs\r\n  ON arcs.replica_id = drs.replica_id\r\nJOIN sys.availability_groups ag \r\n  ON ag.group_id = arcs.group_id\r\nJOIN sys.dm_hadr_availability_replica_states ars \r\n  ON ars.replica_id = arcs.replica_id\r\nWHERE drs.is_local = 0', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 1, NULL, 'Discovery of the non-local (not local to the SQL Server instance) availability databases.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, 'f9691782f89c459c849c00c68775b4bd', 'NON-LOCAL DATABASE DISCOVERY');
INSERT INTO `items` VALUES (917, 11, '', 20, 'Replication discovery', 'db.odbc.discovery[replicas,\"{$MSSQL.DSN}\"]', '1h', '90d', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT ag.name as group_name,\r\n  arcs.replica_server_name as replica_name\r\nFROM sys.dm_hadr_availability_replica_cluster_states as arcs\r\nJOIN sys.availability_groups ag \r\n  ON ag.group_id = arcs.group_id\r\nJOIN sys.dm_hadr_availability_replica_states ars \r\n  ON ars.replica_id = arcs.replica_id', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 1, NULL, 'Discovery of the database replicas.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '8c279cd6442f48bb9c6001ea6d3acfa0', 'REPLICATION DISCOVERY');
INSERT INTO `items` VALUES (918, 11, '', 20, 'MSSQL AG \'{#GROUP_NAME}\': Get replica states', 'db.odbc.get[{#GROUP_NAME}_replica_states,\"{$MSSQL.DSN}\"]', '1m', '0', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT \r\n  ag.name as group_name, \r\n  ISNULL(ags.primary_recovery_health, 2) as primary_recovery_health, \r\n  ISNULL(ags.primary_replica, \'Unknown\') as primary_replica, \r\n  ISNULL(\r\n    ags.secondary_recovery_health, 2\r\n  ) as secondary_recovery_health, \r\n  ags.synchronization_health as synchronization_health \r\nFROM \r\n  sys.dm_hadr_availability_group_states ags \r\nJOIN sys.availability_groups ag ON ag.group_id = ags.group_id \r\n  and ag.name = \'{#GROUP_NAME}\'', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 2, NULL, 'Getting replica states - name, primary and secondary health, synchronization health.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '7cdafac918494002a219345f648747ba', 'MSSQL AG \'{#GROUP_NAME}\': GET REPLICA STATES');
INSERT INTO `items` VALUES (919, 11, '', 20, 'MSSQL AG \'{#GROUP_NAME}\' Local DB \'{#DBNAME}\': Get local DB states', 'db.odbc.get[\"{#GROUP_NAME}_{#DBNAME}_local_db.states\",\"{$MSSQL.DSN}\"]', '1m', '0', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT \r\n  drs.database_state as database_state, \r\n  drs.is_suspended as is_suspended, \r\n  drs.synchronization_health as synchronization_health, \r\n  ag.name as group_name, \r\n  arcs.replica_server_name as replica_name, \r\n  db_name(drs.database_id) as dbname \r\nFROM \r\n  sys.dm_hadr_database_replica_states drs \r\nJOIN sys.dm_hadr_availability_replica_cluster_states arcs ON arcs.replica_id = drs.replica_id \r\n  AND arcs.replica_server_name = \'{#REPLICA_NAME}\' \r\nJOIN sys.availability_groups ag ON ag.group_id = arcs.group_id \r\n  AND ag.name = \'{#GROUP_NAME}\' \r\nJOIN sys.dm_hadr_availability_replica_states ars ON ars.replica_id = arcs.replica_id \r\n  AND db_name(drs.database_id) = \'{#DBNAME}\'', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 2, NULL, 'Getting the states of the local availability database.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '7eda3060b4bc446fb458bec5967b7d3c', 'MSSQL AG \'{#GROUP_NAME}\' LOCAL DB \'{#DBNAME}\': GET LOCAL DB STATES');
INSERT INTO `items` VALUES (920, 11, '', 20, 'MSSQL Mirroring \'{#DBNAME}\': Get the mirror state', 'db.odbc.get[\"{#DBNAME}_mirroring_state\",\"{$MSSQL.DSN}\"]', '1m', '0', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT \r\n  ISNULL(m.mirroring_role, 0) as mirroring_role, \r\n  ISNULL(m.mirroring_role_sequence, 0) as mirroring_role_sequence, \r\n  ISNULL(m.mirroring_state, 7) as mirroring_state, \r\n  ISNULL(m.mirroring_witness_state, 3) as mirroring_witness_state, \r\n  ISNULL(m.mirroring_safety_level, 3) as mirroring_safety_level, \r\n  db_name(m.database_id) as dbname \r\nFROM \r\n  sys.database_mirroring as m \r\nWHERE \r\n  db_name(m.database_id) = \'{#DBNAME}\' \r\n  AND m.mirroring_state_desc IS NOT NULL', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 2, NULL, 'Getting mirrors state', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '082fb332849e4633a169f1a6dbe3a8c5', 'MSSQL MIRRORING \'{#DBNAME}\': GET THE MIRROR STATE');
INSERT INTO `items` VALUES (921, 11, '', 20, 'MSSQL AG \'{#GROUP_NAME}\' Non-Local DB \'*{#REPLICA_NAME}*{#DBNAME}\': Get non-local DB states', 'db.odbc.get[\"{#GROUP_NAME}*{#REPLICA_NAME}*{#DBNAME}_non-local_db.states\",\"{$MSSQL.DSN}\"]', '1m', '0', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT \r\n  ISNULL(drs.log_send_queue_size, 0) as log_send_queue_size, \r\n  ISNULL(drs.redo_queue_size, 0) as redo_queue_size, \r\n  ag.name as group_name, \r\n  arcs.replica_server_name as replica_name, \r\n  db_name(drs.database_id) as dbname \r\nFROM \r\n  sys.dm_hadr_database_replica_states drs \r\nJOIN sys.dm_hadr_availability_replica_cluster_states arcs ON arcs.replica_id = drs.replica_id \r\n  AND arcs.replica_server_name = \'{#REPLICA_NAME}\' \r\nJOIN sys.availability_groups ag ON ag.group_id = arcs.group_id \r\n  AND ag.name = \'{#GROUP_NAME}\' \r\nJOIN sys.dm_hadr_availability_replica_states ars ON ars.replica_id = arcs.replica_id \r\n  AND db_name(drs.database_id) = \'{#DBNAME}\'', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 2, NULL, 'Getting the states of the non-local availability database.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, 'da05efa5ffab475fa95718e8e0069c80', 'MSSQL AG \'{#GROUP_NAME}\' NON-LOCAL DB \'*{#REPLICA_NAME}*{#DBNAME}\': GET NON-LOCAL DB STATES');
INSERT INTO `items` VALUES (922, 11, '', 20, 'MSSQL AG \'{#GROUP_NAME}\' Replica \'{#REPLICA_NAME}\': Get the replica state', 'db.odbc.get[\"{#GROUP_NAME}_{#REPLICA_NAME}_replica.state\",\"{$MSSQL.DSN}\"]', '1m', '0', '0', 0, 4, '', '', '', '', NULL, NULL, 'SELECT \r\n  ars.connected_state as connected_state, \r\n  ars.is_local as is_local, \r\n  arcs.join_state as join_state, \r\n  ISNULL(ars.operational_state, 6) as operational_state, \r\n  ISNULL(ars.recovery_health, 2) as recovery_health, \r\n  ars.role as role, \r\n  ars.synchronization_health as synchronization_health, \r\n  ag.name as group_name, \r\n  arcs.replica_server_name as replica_name \r\nFROM \r\n  sys.dm_hadr_availability_replica_cluster_states as arcs \r\nJOIN sys.availability_groups ag ON ag.group_id = arcs.group_id \r\n  AND ag.name = \'{#GROUP_NAME}\' \r\nJOIN sys.dm_hadr_availability_replica_states ars ON ars.replica_id = arcs.replica_id\r\n  AND arcs.replica_server_name = \'{#REPLICA_NAME}\'', '', 0, '{$MSSQL.USER}', '{$MSSQL.PASSWORD}', '', '', 2, NULL, 'Getting the database replica states.', 0, '30d', 0, '', NULL, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '287c420d8d5346d7a21de580ce1836eb', 'MSSQL AG \'{#GROUP_NAME}\' REPLICA \'{#REPLICA_NAME}\': GET THE REPLICA STATE');
