INSERT INTO `items` VALUES (926, 18, '', 20, 'MSSQL AG \'{#GROUP_NAME}\': Synchronization health', 'mssql.synchronization_health[\"{#GROUP_NAME}\"]', '0', '7d', '365d', 0, 3, '', '', '', '', NULL, 59, '', '', 0, '', '', '', '', 2, NULL, 'Reflects a rollup of the synchronization_health of all availability replicas in the availability group:\r\n0: Not healthy. None of the availability replicas have a healthy synchronization.\r\n1: Partially healthy. The synchronization of some, but not all, availability replicas is healthy.\r\n2: Healthy. The synchronization of every availability replica is healthy.', 0, '30d', 0, '', 918, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, 'fe59d23d0ba44777b0abf1a35b2c014e', 'MSSQL AG \'{#GROUP_NAME}\': SYNCHRONIZATION HEALTH');
