INSERT INTO `items` VALUES (930, 18, '', 20, 'MSSQL AG \'{#GROUP_NAME}\' Local DB \'{#DBNAME}\': Suspended', 'mssql.local_db.is_suspended[\"{#DBNAME}\"]', '0', '7d', '365d', 0, 3, '', '', '', '', NULL, NULL, '', '', 0, '', '', '', '', 2, NULL, 'Database state:\r\n0 = Resumed\r\n1 = Suspended', 0, '30d', 0, '', 919, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '7e4baf2802394b93bf5770f4f72d7f05', 'MSSQL AG \'{#GROUP_NAME}\' LOCAL DB \'{#DBNAME}\': SUSPENDED');
INSERT INTO `items` VALUES (931, 18, '', 20, 'MSSQL AG \'{#GROUP_NAME}\' Local DB \'{#DBNAME}\': State', 'mssql.local_db.state[\"{#DBNAME}\"]', '0', '7d', '365d', 0, 3, '', '', '', '', NULL, 60, '', '', 0, '', '', '', '', 2, NULL, '0 = Online\r\n1 = Restoring\r\n2 = Recovering\r\n3 = Recovery pending\r\n4 = Suspect\r\n5 = Emergency\r\n6 = Offline', 0, '30d', 0, '', 919, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, 'f8111429599e49bd947a12b9771d1a3d', 'MSSQL AG \'{#GROUP_NAME}\' LOCAL DB \'{#DBNAME}\': STATE');
INSERT INTO `items` VALUES (932, 18, '', 20, 'MSSQL AG \'{#GROUP_NAME}\' Local DB \'{#DBNAME}\': Synchronization health', 'mssql.local_db.synchronization_health[\"{#DBNAME}\"]', '0', '7d', '365d', 0, 3, '', '', '', '', NULL, 59, '', '', 0, '', '', '', '', 2, NULL, 'Reflects the intersection of the synchronization state of a database that is joined to the availability group on the availability replica and the availability mode of the availability replica (synchronous-commit or asynchronous-commit mode):\r\n0 = Not healthy. The synchronization_state of the database is 0 (NOT SYNCHRONIZING).\r\n1 = Partially healthy. A database on a synchronous-commit availability replica is considered partially healthy if synchronization_state is 1 (SYNCHRONIZING).\r\n2 = Healthy. A database on an synchronous-commit availability replica is considered healthy if synchronization_state is 2 (SYNCHRONIZED), and a database on an asynchronous-commit availability replica is considered healthy if synchronization_state is 1 (SYNCHRONIZING).', 0, '30d', 0, '', 919, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, 'e7d7fbac95e94c45b6515c8e1b7baf1a', 'MSSQL AG \'{#GROUP_NAME}\' LOCAL DB \'{#DBNAME}\': SYNCHRONIZATION HEALTH');
