INSERT INTO `items` VALUES (934, 18, '', 20, 'MSSQL Mirroring \'{#DBNAME}\': Role sequence', 'mssql.mirroring.role_sequence[\"{#DBNAME}\"]', '0', '7d', '365d', 0, 3, '', '', '', '', NULL, NULL, '', '', 0, '', '', '', '', 2, NULL, 'The number of times that mirroring partners have switched the principal and mirror roles due to a failover or forced service.', 0, '30d', 0, '', 920, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '19cdd945ced447d28b7480c07c1f61bc', 'MSSQL MIRRORING \'{#DBNAME}\': ROLE SEQUENCE');
INSERT INTO `items` VALUES (935, 18, '', 20, 'MSSQL Mirroring \'{#DBNAME}\': Safety level', 'mssql.mirroring.safety_level[\"{#DBNAME}\"]', '0', '7d', '365d', 0, 3, '', '', '', '', NULL, 64, '', '', 0, '', '', '', '', 2, NULL, 'Safety setting for updates on the mirror database:\r\n0 = Unknown state\r\n1 = Off [asynchronous]\r\n2 = Full [synchronous]', 0, '30d', 0, '', 920, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, 'bedb23d788074424afdb2751c8a7e2b7', 'MSSQL MIRRORING \'{#DBNAME}\': SAFETY LEVEL');
INSERT INTO `items` VALUES (936, 18, '', 20, 'MSSQL Mirroring \'{#DBNAME}\': State', 'mssql.mirroring.state[\"{#DBNAME}\"]', '0', '7d', '365d', 0, 3, '', '', '', '', NULL, 63, '', '', 0, '', '', '', '', 2, NULL, 'State of the mirror database and of the database mirroring session.\r\n0 = Suspended\r\n1 = Disconnected from the other partner\r\n2 = Synchronizing\r\n3 = Pending Failover\r\n4 = Synchronized\r\n5 = The partners are not synchronized. Failover is not possible now.\r\n6 = The partners are synchronized. Failover is potentially possible. For information about the requirements for the failover, see Database Mirroring Operating Modes.', 0, '30d', 0, '', 920, '3s', '', '', '', '200', 1, 0, '', '', 0, 0, 0, '', '', '', 0, 0, 0, 0, '5df06f00a6194242af45ff5cccf43988', 'MSSQL MIRRORING \'{#DBNAME}\': STATE');
