INSERT INTO `items` VALUES (1328, 19, '', 28, 'Nginx: Get stub status page', 'nginx.get_stub_status', '1m', '1h', '0', 0, 4, '', '', '', '', NULL, NULL, '', '', 0, '', '', '', '', 0, NULL, 'The following status information is provided:\r\n`Active connections` - the current number of active client connections including waiting connections.\r\n`Accepted` - the total number of accepted client connections.\r\n`Handled` - the total number of handled connections. Generally, the parameter value is the same as for the accepted connections, unless some resource limits have been reached (for example, the `worker_connections` limit).\r\n`Requests` - the total number of client requests.\r\n`Reading` - the current number of connections where Nginx is reading the request header.\r\n`Writing` - the current number of connections where Nginx is writing a response back to the client.\r\n`Waiting` - the current number of idle client connections waiting for a request.\r\n\r\nSee also [Module ngx_http_stub_status_module](https://nginx.org/en/docs/http/ngx_http_stub_status_module.html).', 0, '30d', 0, '', NULL, '3s', '{$NGINX.STUB_STATUS.SCHEME}://{HOST.CONN}:{$NGINX.STUB_STATUS.PORT}/{$NGINX.STUB_STATUS.PATH}', '', '', '200', 1, 0, '', '', 2, 0, 0, '', '', '', 0, 0, 0, 0, '86f93d1941d147fe94d754eddd3e8ff2', 'NGINX: GET STUB STATUS PAGE');
