INSERT INTO `triggers` VALUES (175, '{323}<{$VFS.FS.INODE.PFREE.MIN.CRIT:\"{#FSNAME}\"}', '{#FSNAME}: Running out of free inodes', '', 0, 0, 3, 0, 'It may become impossible to write to a disk if there are no index nodes left.\r\nThe following error messages may be returned as symptoms, even though the free space is available:\r\n- \'No space left on device\';\r\n- \'Disk is full\'.', '', NULL, 0, 0, 2, 0, '', 0, '', 0, 'Free inodes: {ITEM.LASTVALUE1}', 0, '{#FSNAME}: Running out of free inodes (free < {$VFS.FS.INODE.PFREE.MIN.CRIT:\"{#FSNAME}\"}%)', 'da93749fb6cb4eb785b35a54f099b6d7', '', '{323}<{$VFS.FS.INODE.PFREE.MIN.CRIT:\"{#FSNAME}\"}');
