INSERT INTO `triggers` VALUES (300, '{506} > {$MSSQL.PERCENT_COMPILATIONS.MAX}', 'MSSQL: Percent of adhoc queries running is high', '', 0, 0, 2, 0, 'The lower this value is the better. High values often indicate excessive adhoc querying and should be as low as possible. If excessive adhoc querying is happening, try rewriting the queries as procedures or invoke the queries using sp_executeSQL. When rewriting isn\'t possible, consider using a plan guide or setting the database to parameterization forced mode.', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, 'MSSQL: Percent of adhoc queries running is over {$MSSQL.PERCENT_COMPILATIONS.MAX}% for 15m', 'c5c1840220cd44fcaa7b47e0d52309bb', '', '{506} > {$MSSQL.PERCENT_COMPILATIONS.MAX}');
INSERT INTO `triggers` VALUES (301, '{507} > {$MSSQL.PERCENT_RECOMPILATIONS.MAX}', 'MSSQL: Percent of times statement recompiles is high', '', 0, 0, 2, 0, 'This number should be at or near zero, since recompiles can cause deadlocks and exclusive compile locks. This counter\'s value should follow in proportion to \"Batch Requests/sec\" and \"SQL Compilations/sec\".', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, 'MSSQL: Percent of times statement recompiles is over {$MSSQL.PERCENT_RECOMPILATIONS.MAX}% for 15m', 'd5bad8ead8704e00bf332032e22ebfdd', '', '{507} > {$MSSQL.PERCENT_RECOMPILATIONS.MAX}');
INSERT INTO `triggers` VALUES (302, '{508} > 0.001', 'MSSQL: Number of index and table scans exceeds index searches in the last 15m', '', 0, 0, 2, 0, 'Index searches are preferable to index and table scans. For OLTP applications, optimize for more index searches and less scans (preferably, 1 full scan for every 1000 index searches). Index and table scans are expensive I/O operations.', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, '', '3c22a74a94d641389b57a6013ddd10cb', '', '{508} > 0.001');
