INSERT INTO `triggers` VALUES (310, '{518} * 100 > 10 * {519}', 'MSSQL: Too frequently using pointers', '', 0, 0, 2, 0, 'Rows with varchar columns can experience expansion when varchar values are updated with a longer string.  In the case where the row cannot fit in the existing page, the row migrates and access to the row will traverse a pointer.  This only happens on heaps (tables without clustered indexes). Evaluate clustered index for heap tables.  In cases where clustered indexes cannot be used, drop non-clustered indexes, build a clustered index to reorg pages and rows, drop the clustered index, then recreate non-clustered indexes.', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, '', 'a1397bd4e9334a24a34dba0687b263e6', '', '{518} * 100 > 10 * {519}');
INSERT INTO `triggers` VALUES (311, '{520} > {$MSSQL.PERCENT_READAHEAD.MAX} / 100 * {521}', 'MSSQL: Too many physical reads occurring', '', 0, 0, 2, 0, 'If this value makes up even a sizeable minority of the total Page Reads/sec (say, greater than 20% of the total page reads), you may have too many physical reads occurring.', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, '', '2a58729eb605461b853ec4e855878226', '', '{520} > {$MSSQL.PERCENT_READAHEAD.MAX} / 100 * {521}');
