INSERT INTO `triggers` VALUES (350, '{569} > {$ORACLE.SESSION.LONG.LOCK.MAX.WARN}', 'Oracle: Too many sessions locked', '', 0, 0, 2, 0, 'The number of locked sessions exceeding {$ORACLE.SESSION.LOCK.MAX.TIME} seconds is too high. Long-term locks can negatively affect the database performance. Therefore, if they are detected, you should first find the most difficult queries from the database point of view and then analyze possible resource leaks.', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, 'Oracle: Too many sessions locked over {$ORACLE.SESSION.LOCK.MAX.TIME}s (over {$ORACLE.SESSION.LONG.LOCK.MAX.WARN} for 5 min)', 'a8f5ec966f5e4c72baad1380eda4d0a2', '', '{569} > {$ORACLE.SESSION.LONG.LOCK.MAX.WARN}');
