INSERT INTO `triggers` VALUES (421, '{661}<{$KAFKA.REQUEST_HANDLER_AVG_IDLE.MIN.WARN}', 'Kafka: Request handler average idle percent is too low', '', 0, 0, 3, 0, 'The request handler idle ratio metric indicates the percentage of time the request handlers are not in use. The lower this number, the more loaded the broker is.', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, 'Kafka: Request handler average idle percent is too low (under {$KAFKA.REQUEST_HANDLER_AVG_IDLE.MIN.WARN} for 15m)', '8f070e06520b4b299fcfffa952d318fc', '', '{661}<{$KAFKA.REQUEST_HANDLER_AVG_IDLE.MIN.WARN}');
INSERT INTO `triggers` VALUES (422, '{662}>0', 'Kafka: There are partitions under the min ISR', '', 0, 0, 3, 0, 'The Under min ISR partitions metric displays the number of partitions, where the number of In-Sync Replicas (ISR) is less than the minimum number of in-sync replicas specified. The two most common causes of under-min ISR partitions are that one or more brokers is unresponsive, or the cluster is experiencing performance issues and one or more brokers are falling behind.', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, '', 'b401a90e75cd4a478d4f8b8cc4f15dc2', '', '{662}>0');
INSERT INTO `triggers` VALUES (423, '{663}>0', 'Kafka: There are under replicated partitions', '', 0, 0, 3, 0, 'The Under replicated partitions metric displays the number of partitions that do not have enough replicas to meet the desired replication factor. A partition will also be considered under-replicated if the correct number of replicas exist, but one or more of the replicas have fallen significantly behind the partition leader. The two most common causes of under-replicated partitions are that one or more brokers is unresponsive, or the cluster is experiencing performance issues and one or more brokers have fallen behind.', '', NULL, 0, 0, 0, 0, '', 0, '', 0, '', 0, '', '0ecd8b4b3db6489b83d4a1f78faee03c', '', '{663}>0');
