INSERT INTO `triggers` VALUES (495, '({754}>({$IF.UTIL.MAX:\"{#IFNAME}\"}/100)*{755} or\r\n{756}>({$IF.UTIL.MAX:\"{#IFNAME}\"}/100)*{755}) and\r\n{755}>0', 'Interface {#IFNAME}: High bandwidth usage', '', 0, 0, 2, 0, 'The utilization of the network interface is close to its estimated maximum bandwidth.', '', 66, 0, 0, 2, 1, '{754}<(({$IF.UTIL.MAX:\"{#IFNAME}\"}-3)/100)*{755} and\r\n{756}<(({$IF.UTIL.MAX:\"{#IFNAME}\"}-3)/100)*{755}', 0, '', 1, 'In: {ITEM.LASTVALUE1}, out: {ITEM.LASTVALUE3}, speed: {ITEM.LASTVALUE2}', 0, 'Interface {#IFNAME}: High bandwidth usage (>{$IF.UTIL.MAX:\"{#IFNAME}\"}%)', '', '', '({754}>({$IF.UTIL.MAX:\"{#IFNAME}\"}/100)*{755} or\r\n{756}>({$IF.UTIL.MAX:\"{#IFNAME}\"}/100)*{755}) and\r\n{755}>0');
INSERT INTO `triggers` VALUES (496, '{757}>{$IF.ERRORS.WARN:\"{#IFNAME}\"}\r\nor {758}>{$IF.ERRORS.WARN:\"{#IFNAME}\"}', 'Interface {#IFNAME}: High error rate', '', 0, 0, 2, 0, 'It recovers when it is below 80% of the `{$IF.ERRORS.WARN:\"{#IFNAME}\"}` threshold.', '', 67, 0, 0, 2, 1, '{759}<{$IF.ERRORS.WARN:\"{#IFNAME}\"}*0.8\r\nand {760}<{$IF.ERRORS.WARN:\"{#IFNAME}\"}*0.8', 0, '', 1, 'errors in: {ITEM.LASTVALUE1}, errors out: {ITEM.LASTVALUE2}', 0, 'Interface {#IFNAME}: High error rate (>{$IF.ERRORS.WARN:\"{#IFNAME}\"} for 5m)', '', '', '{757}>{$IF.ERRORS.WARN:\"{#IFNAME}\"}\r\nor {758}>{$IF.ERRORS.WARN:\"{#IFNAME}\"}');
INSERT INTO `triggers` VALUES (497, '{$IFCONTROL:\"{#IFNAME}\"}=1 and {761}=2 and ({762}<>{763})', 'Interface {#IFNAME}: Link down', '', 0, 0, 3, 0, 'This trigger expression works as follows:\r\n1. It can be triggered if the operations status is down.\r\n2. `{$IFCONTROL:\"{#IFNAME}\"}=1` - a user can redefine context macro to value - 0. That marks this interface as not important. No new trigger will be fired if this interface is down.\r\n3. `{TEMPLATE_NAME:METRIC.diff()}=1` - the trigger fires only if the operational status was up to (1) sometime before (so, do not fire for the \'eternal off\' interfaces.)\r\n\r\nWARNING: if closed manually - it will not fire again on the next poll, because of .diff.', '', 68, 0, 0, 2, 1, '{761}<>2 or {$IFCONTROL:\"{#IFNAME}\"}=0', 0, '', 1, 'Current state: {ITEM.LASTVALUE1}', 0, '', '', '', '{$IFCONTROL:\"{#IFNAME}\"}=1 and {761}=2 and ({762}<>{763})');
INSERT INTO `triggers` VALUES (498, '{764} > {$VFS.DEV.READ.AWAIT.WARN:\"{#DEVNAME}\"} or {765} > {$VFS.DEV.WRITE.AWAIT.WARN:\"{#DEVNAME}\"}', '{#DEVNAME}: Disk read/write request responses are too high', '', 0, 0, 2, 0, 'This trigger might indicate the disk `{#DEVNAME}` saturation.', '', 69, 0, 0, 2, 0, '', 0, '', 1, '', 0, '{#DEVNAME}: Disk read/write request responses are too high (read > {$VFS.DEV.READ.AWAIT.WARN:\"{#DEVNAME}\"} ms for 15m or write > {$VFS.DEV.WRITE.AWAIT.WARN:\"{#DEVNAME}\"} ms for 15m)', '', '', '{764} > {$VFS.DEV.READ.AWAIT.WARN:\"{#DEVNAME}\"} or {765} > {$VFS.DEV.WRITE.AWAIT.WARN:\"{#DEVNAME}\"}');
INSERT INTO `triggers` VALUES (499, '{766}>{$VFS.FS.PUSED.MAX.CRIT:\"{#FSNAME}\"} and\r\n(({767}-{768})<{$VFS.FS.FREE.MIN.CRIT:\"{#FSNAME}\"} or {769}<1d)', '{#FSNAME}: Disk space is critically low', '', 0, 0, 3, 0, 'Two conditions should match:\r\n1. The first condition - utilization of the space should be above `{$VFS.FS.PUSED.MAX.CRIT:\"{#FSNAME}\"}`.\r\n2. The second condition should be one of the following:\r\n- the disk free space is less than `{$VFS.FS.FREE.MIN.CRIT:\"{#FSNAME}\"}`;\r\n- the disk will be full in less than 24 hours.', '', 70, 0, 0, 2, 0, '', 0, '', 1, 'Space used: {ITEM.LASTVALUE3} of {ITEM.LASTVALUE2} ({ITEM.LASTVALUE1})', 0, '{#FSNAME}: Disk space is critically low (used > {$VFS.FS.PUSED.MAX.CRIT:\"{#FSNAME}\"}%)', '', '', '{766}>{$VFS.FS.PUSED.MAX.CRIT:\"{#FSNAME}\"} and\r\n(({767}-{768})<{$VFS.FS.FREE.MIN.CRIT:\"{#FSNAME}\"} or {769}<1d)');
INSERT INTO `triggers` VALUES (500, '{770}>{$VFS.FS.PUSED.MAX.WARN:\"{#FSNAME}\"} and\r\n(({771}-{772})<{$VFS.FS.FREE.MIN.WARN:\"{#FSNAME}\"} or {773}<1d)', '{#FSNAME}: Disk space is low', '', 0, 0, 2, 0, 'Two conditions should match:\r\n1. The first condition - utilization of the space should be above `{$VFS.FS.PUSED.MAX.WARN:\"{#FSNAME}\"}`.\r\n2. The second condition should be one of the following:\r\n- the disk free space is less than `{$VFS.FS.FREE.MIN.WARN:\"{#FSNAME}\"}`;\r\n- the disk will be full in less than 24 hours.', '', 71, 0, 0, 2, 0, '', 0, '', 1, 'Space used: {ITEM.LASTVALUE3} of {ITEM.LASTVALUE2} ({ITEM.LASTVALUE1})', 0, '{#FSNAME}: Disk space is low (used > {$VFS.FS.PUSED.MAX.WARN:\"{#FSNAME}\"}%)', '', '', '{770}>{$VFS.FS.PUSED.MAX.WARN:\"{#FSNAME}\"} and\r\n(({771}-{772})<{$VFS.FS.FREE.MIN.WARN:\"{#FSNAME}\"} or {773}<1d)');
INSERT INTO `triggers` VALUES (501, '{774}<{$VFS.FS.INODE.PFREE.MIN.CRIT:\"{#FSNAME}\"}', '{#FSNAME}: Running out of free inodes', '', 0, 0, 3, 0, 'It may become impossible to write to a disk if there are no index nodes left.\r\nThe following error messages may be returned as symptoms, even though the free space is available:\r\n- \'No space left on device\';\r\n- \'Disk is full\'.', '', 72, 0, 0, 2, 0, '', 0, '', 0, 'Free inodes: {ITEM.LASTVALUE1}', 0, '{#FSNAME}: Running out of free inodes (free < {$VFS.FS.INODE.PFREE.MIN.CRIT:\"{#FSNAME}\"}%)', '', '', '{774}<{$VFS.FS.INODE.PFREE.MIN.CRIT:\"{#FSNAME}\"}');
INSERT INTO `triggers` VALUES (502, '{775}<{$VFS.FS.INODE.PFREE.MIN.WARN:\"{#FSNAME}\"}', '{#FSNAME}: Running out of free inodes', '', 0, 0, 2, 0, 'It may become impossible to write to a disk if there are no index nodes left.\r\nThe following error messages may be returned as symptoms, even though the free space is available:\r\n- \'No space left on device\';\r\n- \'Disk is full\'.', '', 73, 0, 0, 2, 0, '', 0, '', 0, 'Free inodes: {ITEM.LASTVALUE1}', 0, '{#FSNAME}: Running out of free inodes (free < {$VFS.FS.INODE.PFREE.MIN.WARN:\"{#FSNAME}\"}%)', '', '', '{775}<{$VFS.FS.INODE.PFREE.MIN.WARN:\"{#FSNAME}\"}');
